import { produce } from 'immer';
import { ADD_TODO,ON_EDIT_TODO} from '../redux/actions';

const initialState = {
  todoList: [],
};

const listTodo = (state = initialState, action) => {
  switch (action.type) {
    case ADD_TODO:
      return produce(preState, (newState) => {
        newState.todoList.push(action.payload);
      });


    case ON_EDIT_TODO:
      return produce(preState, (newState) => {
        
        const { todo, id } = action.payload;

        if(id >=0) {
          newState.todoList.map(item => item.id===id ? ({...item, name: todo.name}) : item);
        }
       });
    case DELETE_ITEM:
      return produce(preState, (newState) => {
          newState.todoList = newState.todoList.filter(item => item.id !== action.payload);
      });
      
    default:
      return state;
  }
};

export default listTodo;
