import { produce } from 'immer';
import { ADD_TODO,ON_EDIT_TODO} from '../redux/actions';

const initialState = {
  todoList: [],
};

const listTodo = (state = initialState, action) => {
  switch (action.type) {
    case ADD_TODO:
      return produce(state, (draft) => {
        draft.todoList.push(action.payload);
      });


    case ON_EDIT_TODO:
      return produce(state, (draft) => {
        
        const { todo, id } = action.payload;

        if(id >=0) {
              draft.todoList.map(item => item.id===id ? ({...item, name: todo.name}) : item);
        }
       });
    case DELETE_ITEM:
      return produce(state, (draft) => {
          draft.items = draft.items.filter(item => item.itemId !== action.payload);
        });
      
    default:
      return state;
  }
};

export default listTodo;
