import {  produce } from 'immer';
import { SET_FILTER, FILTER } from '../redux/actions';
const filterState = FILTER.ALL;

const filterItems = (currentState = filterState, action) => {
    switch ( action.type) {
        case SET_FILTER: 
          return produce(currentState, draftState=> {
            draftState.filter = action.payload;
          });
        default:
            return currentState;
    }
}

export default filterItems;
