import {
    ADD_TODO,
    GET_TODO_EDIT_ID,
    ON_EDIT_TODO
} from '../actions/index'

const initialState = {
    todoList : [{
        id: 1,
        name: 'todo1',
        isCheck: true
    },{
        id: 2,
        name: 'todo2',
        isCheck: false
    }
    ],
    todoEditId: ''
}
const todosReducer = (state = initialState, action) => {
    const { todoList } = state
    const list = JSON.parse(JSON.stringify(todoList))
    switch (action.type) {
        case ADD_TODO:
            return Object.assign({}, state, {
                todoList: [...list,action.todo]
            })
        case GET_TODO_EDIT_ID:
            return Object.assign({}, state, {
                todoEditId : action.id
            })
        case ON_EDIT_TODO:
            if( action.id >= 0) {
                list.splice(action.id, 1, action.todo)
            }
            return Object.assign({}, state, {
                todoList : list,
                todoEditId :''
            
            })
        default:
            return state
    }
}
export default todosReducer
