import {
    ADD_TODO,
    GET_TODO_EDIT_ID,
    ON_EDIT_TODO,
    MARK_COMPLETED,
    CHECK_ALL_TODO
} from '../actions/index'

const initialState = {
    todoList : [{
        id: 1,
        name: 'todo1',
        isCheck: true
    },{
        id: 2,
        name: 'todo2',
        isCheck: false
    }],
    todoEditId:'',
    nameEdit:'',
    isCheckAll: false,
    status: 'ALL'
}
const todosReducer = (state = initialState, action) => {
    const { todoList } = state
    const list = JSON.parse(JSON.stringify(todoList))
    switch (action.type) {
        case ADD_TODO:
            return Object.assign({}, state, {
                todoList: [...list,action.todo]
            })
        case GET_TODO_EDIT_ID:
            if(action.id >=0) {
                const todo = todoList.find(todo => id === todo.id);
            }
            return Object.assign({}, state, {
                todoEditId: action.id
               // nameEdit: todo.name
            })
        case ON_EDIT_TODO:
            if( action.id >= 0) {
                todoList.splice(action.id, 1, action.todo)
            }
            return Object.assign({}, state, {
                todoList : list,
                //todoEditId :''
            
            })
        case MARK_COMPLETED:
            const updatedList = todoList.map(todo =>  todo.id ===id ? ({...todo, isCheck: !todo.isCheck}) : todo )
            return Object.assign({}, state, {
                todoList : updatedList,
                isCheckAll : !isNotCheckAll(updatedList)
            })
        case CHECK_ALL_TODO:
            return Object.assign({}, state, {
            })
        default:
            return state
    }
}
export default todosReducer
