import { produce } from 'immer';
import { ADD_TODO,ON_EDIT_TODO} from '../redux/actions';

const initialState = {
  todoList: [],
};

const listTodo = (state = initialState, action) => {
  switch (action.type) {
    case ADD_TODO:
      return produce(state, (draft) => {
        draft.todoList.push(action.payload);
      });


    case ON_EDIT_TODO:
      return produce(state, (draft) => {
        const { itemId, content } = action.payload;
        const item = draft.items.find(item => item.itemId === itemId);
        if (item) {
          item.content = content;
        }
      });
    default:
      return state;
  }
};

export default listTodo;
